$(document).ready(function(){
    $(".frm-event").keyup(function(e){
        if($(this).val().length + 1 > 0){
          $(this).css("background-color","#fff");
          $(this).css("border-radius","100px");
          $(this).css("box-shadow","0 9px 18px 0 rgba(168, 172, 185, 0.62)", "inset 0 1px 3px 0 rgba(0, 0, 0, 0.5)", "inset 0 1px 3px 0 rgba(0, 0, 0, 0.5)");
          $(this).css("border-color","transparent");
        }
        if($(this).val().length === 0){
          $(this).css("background-color","#EFF0F4");
          $(this).css("border-radius","100px");
          $(this).css("box-shadow","none");
        }
    });
    $(".frm-event-textarea").keyup(function(e){
        if($(this).val().length + 1 > 0){
          $(this).css("background-color","#fff");
          $(this).css("border-radius","10px");
          $(this).css("box-shadow","0 9px 18px 0 rgba(168, 172, 185, 0.62)", "inset 0 1px 3px 0 rgba(0, 0, 0, 0.5)", "inset 0 1px 3px 0 rgba(0, 0, 0, 0.5)");
          $(this).css("border-color","transparent");
        }
        if($(this).val().length === 0){
          $(this).css("background-color","#EFF0F4");
          $(this).css("border-radius","10px");
          $(this).css("width","440px");
          $(this).css("height","110px");
          $(this).css("resize","none");
        }
    });

});